    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>detail</span> <span class="token attr-name">v-else-if</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>$page.frontmatter.layout == &#39;detail&#39;<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>
